// Waste types available in the system
export const WASTE_TYPES = [
  { value: 'e-waste', label: 'E-Waste (Electronics)', key: 'e_waste_price' },
  { value: 'plastic', label: 'Plastic', key: 'plastic_price' },
  { value: 'organic', label: 'Organic/Food Waste', key: 'organic_price' },
  { value: 'paper', label: 'Paper/Cardboard', key: 'paper_price' },
  { value: 'metal', label: 'Metal', key: 'metal_price' },
  { value: 'glass', label: 'Glass', key: 'glass_price' },
  { value: 'hazardous', label: 'Hazardous Materials', key: 'hazardous_price' },
  { value: 'mixed', label: 'Mixed Waste', key: 'mixed_price' }
];

// Helper function to get price field name from waste type
export const getPriceFieldName = (wasteType) => {
  const type = WASTE_TYPES.find(t => t.value === wasteType);
  return type ? type.key : null;
};

// Helper function to get waste type label
export const getWasteTypeLabel = (wasteType) => {
  const type = WASTE_TYPES.find(t => t.value === wasteType);
  return type ? type.label : wasteType;
};
