import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Container, Row, Col, Card, Button, Badge, Table, Spinner, Alert, Modal, Form } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../services/api';

const Dashboard = ({ auth }) => {
  const navigate = useNavigate();
  const routeLocation = useLocation();
  const { user } = auth;
  const [nearbyCollectors, setNearbyCollectors] = useState([]);
  const [wasteRequests, setWasteRequests] = useState([]);
  const [pendingRequests, setPendingRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [location, setLocation] = useState({ latitude: null, longitude: null });
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [address, setAddress] = useState('');

  useEffect(() => {
    if (user) {
      initializeDashboard();
    }
  }, [user]);

  // Refresh data when user navigates to dashboard (route change)
  useEffect(() => {
    if (user && routeLocation.pathname === '/dashboard' && !loading) {
      loadDashboardData(location.latitude, location.longitude);
    }
  }, [routeLocation.pathname, user]);

  // Refresh data when component gains focus (user returns from other pages)
  useEffect(() => {
    const handleFocus = () => {
      if (user && !loading) {
        loadDashboardData(location.latitude, location.longitude);
      }
    };

    const handleVisibilityChange = () => {
      if (!document.hidden && user && !loading) {
        loadDashboardData(location.latitude, location.longitude);
      }
    };

    window.addEventListener('focus', handleFocus);
    document.addEventListener('visibilitychange', handleVisibilityChange);
    
    return () => {
      window.removeEventListener('focus', handleFocus);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [user, loading, location]);

  const initializeDashboard = async () => {
    try {
      setLoading(true);
      
      // Get user's current location
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          async (position) => {
            const { latitude, longitude } = position.coords;
            setLocation({ latitude, longitude });
            
            // Update user location in backend
            try {
              await api.put('/waste/location', { latitude, longitude });
            } catch (error) {
              console.error('Failed to update location:', error);
            }
            
            // Load dashboard data based on user role
            await loadDashboardData(latitude, longitude);
          },
          (error) => {
            console.error('Location access denied:', error);
            setShowLocationModal(true);
            loadDashboardData();
          }
        );
      } else {
        console.error('Geolocation not supported');
        setShowLocationModal(true);
        loadDashboardData();
      }
    } catch (error) {
      console.error('Dashboard initialization error:', error);
      toast.error('Failed to load dashboard');
    } finally {
      setLoading(false);
    }
  };

  const loadDashboardData = async (lat = null, lng = null) => {
    try {
      if (user.role === 'customer') {
        // Load customer data
        const [collectorsResponse, requestsResponse] = await Promise.all([
          lat && lng 
            ? api.get(`/waste/collectors/nearby?latitude=${lat}&longitude=${lng}&radius=15`)
            : Promise.resolve({ data: { data: [] } }),
          api.get('/waste/requests')
        ]);
        
        setNearbyCollectors(collectorsResponse.data.data || []);
        setWasteRequests(requestsResponse.data.data || []);
      } else if (user.role === 'collector') {
        // Load collector data
        const [pendingResponse, assignedResponse] = await Promise.all([
          lat && lng 
            ? api.get(`/waste/requests/pending?latitude=${lat}&longitude=${lng}&radius=20`)
            : api.get('/waste/requests/pending'),
          api.get('/waste/requests?status=assigned')
        ]);
        
        setPendingRequests(pendingResponse.data.data || []);
        setWasteRequests(assignedResponse.data.data || []);
      }
    } catch (error) {
      console.error('Failed to load dashboard data:', error);
      toast.error('Failed to load dashboard data');
    }
  };

  const handleManualLocation = async () => {
    if (!address.trim()) {
      toast.error('Please enter an address');
      return;
    }
    
    try {
      // For demo purposes, use default coordinates
      // In a real app, you'd use a geocoding service like Google Maps API
      const defaultLat = 40.7128; // Default to NYC coordinates
      const defaultLng = -74.0060;
      
      setLocation({ latitude: defaultLat, longitude: defaultLng });
      await api.put('/waste/location', { 
        latitude: defaultLat, 
        longitude: defaultLng, 
        address 
      });
      
      setShowLocationModal(false);
      await loadDashboardData(defaultLat, defaultLng);
      toast.success('Location updated successfully');
    } catch (error) {
      console.error('Failed to update location:', error);
      toast.error('Failed to update location');
    }
  };

  const handleRequestStatusUpdate = async (requestId, newStatus) => {
    try {
      await api.put(`/waste/requests/${requestId}/status`, { status: newStatus });
      
      // Reload dashboard data
      await loadDashboardData(location.latitude, location.longitude);
      toast.success('Request status updated successfully');
    } catch (error) {
      console.error('Failed to update request status:', error);
      toast.error('Failed to update request status');
    }
  };

  const getStatusBadgeVariant = (status) => {
    switch (status) {
      case 'pending': return 'warning';
      case 'assigned': return 'info';
      case 'in_progress': return 'primary';
      case 'completed': return 'success';
      case 'cancelled': return 'danger';
      default: return 'secondary';
    }
  };

  const formatDistance = (distance) => {
    if (distance < 1) {
      return `${(distance * 1000).toFixed(0)}m`;
    }
    return `${distance.toFixed(1)}km`;
  };

  const customerDashboard = () => (
    <>
      <Row className="mb-4">
        <Col>
          <h2 className="text-primary">Customer Dashboard</h2>
          <p className="text-muted">Welcome back, {user.first_name}! Manage your waste pickup requests</p>
        </Col>
      </Row>
      
      {/* Quick Actions */}
      <Row className="g-4 mb-5">
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-plus-circle fa-3x text-primary"></i>
              </div>
              <Card.Title>Request Pickup</Card.Title>
              <Card.Text>
                Schedule a new waste collection request for your location.
              </Card.Text>
              <Button variant="primary" onClick={() => navigate('/request-pickup')}>
                New Request
              </Button>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-map-marker-alt fa-3x text-success"></i>
              </div>
              <Card.Title>Update Location</Card.Title>
              <Card.Text>
                Update your current location to find nearby collectors.
              </Card.Text>
              <Button variant="success" onClick={() => setShowLocationModal(true)}>
                Set Location
              </Button>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-history fa-3x text-info"></i>
              </div>
              <Card.Title>Request History</Card.Title>
              <Card.Text>
                View and track all your previous pickup requests.
              </Card.Text>
              <Button variant="info" onClick={() => navigate('/my-requests')}>
                View History
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Current Waste Requests */}
      <Row className="mb-5">
        <Col>
          <Card className="shadow-sm">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-tasks me-2"></i>
                Current Waste Requests
              </h5>
              <Button 
                variant="light" 
                size="sm" 
                onClick={() => loadDashboardData(location.latitude, location.longitude)}
                disabled={loading}
              >
                <i className="fas fa-sync-alt me-1"></i>
                Refresh
              </Button>
            </Card.Header>
            <Card.Body>
              {wasteRequests.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-inbox fa-3x text-muted mb-3"></i>
                  <p className="text-muted">No active waste requests. Create your first request!</p>
                  <Button variant="primary" onClick={() => navigate('/request-pickup')}>
                    Create Request
                  </Button>
                </div>
              ) : (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Waste Type</th>
                      <th>Quantity</th>
                      <th>Pickup Address</th>
                      <th>Status</th>
                      <th>Collector</th>
                      <th>Created</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {wasteRequests.map((request) => (
                      <tr key={request.id}>
                        <td>
                          <span className="text-capitalize">{request.waste_type}</span>
                        </td>
                        <td>{request.quantity || 'N/A'}</td>
                        <td className="text-truncate" style={{ maxWidth: '200px' }}>
                          {request.pickup_address}
                        </td>
                        <td>
                          <Badge bg={getStatusBadgeVariant(request.status)}>
                            {request.status.replace('_', ' ').toUpperCase()}
                          </Badge>
                        </td>
                        <td>
                          {request.collector_first_name ? (
                            <div>
                              <strong>{request.collector_first_name} {request.collector_last_name}</strong>
                              <br />
                              <small className="text-muted">{request.collector_phone}</small>
                            </div>
                          ) : (
                            <span className="text-muted">Not assigned</span>
                          )}
                        </td>
                        <td>
                          {new Date(request.created_at).toLocaleDateString()}
                        </td>
                        <td>
                          {request.status === 'pending' && (
                            <Button 
                              size="sm" 
                              variant="outline-danger"
                              onClick={() => handleRequestStatusUpdate(request.id, 'cancelled')}
                            >
                              Cancel
                            </Button>
                          )}
                          {request.status === 'completed' && (
                            <Badge bg="success">Completed</Badge>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Nearby Collectors */}
      <Row>
        <Col>
          <Card className="shadow-sm">
            <Card.Header className="bg-success text-white">
              <h5 className="mb-0">
                <i className="fas fa-users me-2"></i>
                Available Waste Collectors Nearby
              </h5>
            </Card.Header>
            <Card.Body>
              {nearbyCollectors.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-map-marker-alt fa-3x text-muted mb-3"></i>
                  <p className="text-muted">
                    {location.latitude ? 
                      'No collectors found in your area. Try expanding your search radius.' :
                      'Set your location to find nearby collectors.'
                    }
                  </p>
                  <Button variant="success" onClick={() => setShowLocationModal(true)}>
                    Update Location
                  </Button>
                </div>
              ) : (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Contact</th>
                      <th>Distance</th>
                      <th>Service Radius</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {nearbyCollectors.map((collector) => (
                      <tr key={collector.id}>
                        <td>
                          <strong>{collector.first_name} {collector.last_name}</strong>
                          <br />
                          <small className="text-muted">@{collector.username}</small>
                        </td>
                        <td>{collector.phone}</td>
                        <td>
                          <Badge bg="info">{formatDistance(collector.distance)}</Badge>
                        </td>
                        <td>{collector.service_radius}km</td>
                        <td>
                          <Badge bg={collector.is_available ? 'success' : 'warning'}>
                            {collector.is_available ? 'Available' : 'Busy'}
                          </Badge>
                        </td>
                        <td>
                          <Button size="sm" variant="primary" onClick={() => toast.info('Feature coming soon!')}>
                            Request Pickup
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </>
  );

  const collectorDashboard = () => (
    <>
      <Row className="mb-4">
        <Col>
          <h2 className="text-primary">Collector Dashboard</h2>
          <p className="text-muted">Welcome back, {user.first_name}! Manage waste collection requests</p>
        </Col>
      </Row>
      
      {/* Quick Actions */}
      <Row className="g-4 mb-5">
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-search fa-3x text-primary"></i>
              </div>
              <Card.Title>Find Requests</Card.Title>
              <Card.Text>
                Find nearby waste collection requests in your area.
              </Card.Text>
              <Button variant="primary" onClick={() => toast.info('Auto-refreshing...')}>
                Refresh
              </Button>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-toggle-on fa-3x text-success"></i>
              </div>
              <Card.Title>Availability</Card.Title>
              <Card.Text>
                Toggle your availability for new requests.
              </Card.Text>
              <Button variant="success" onClick={() => toast.info('Feature coming soon!')}>
                Available
              </Button>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={6} lg={4}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="text-center">
              <div className="mb-3">
                <i className="fas fa-chart-line fa-3x text-info"></i>
              </div>
              <Card.Title>Statistics</Card.Title>
              <Card.Text>
                View your collection statistics and earnings.
              </Card.Text>
              <Button variant="info" onClick={() => toast.info('Feature coming soon!')}>
                View Stats
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Pending Requests */}
      <Row className="mb-5">
        <Col>
          <Card className="shadow-sm">
            <Card.Header className="bg-warning text-dark">
              <h5 className="mb-0">
                <i className="fas fa-clock me-2"></i>
                Pending Collection Requests
              </h5>
            </Card.Header>
            <Card.Body>
              {pendingRequests.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-clipboard-list fa-3x text-muted mb-3"></i>
                  <p className="text-muted">No pending requests in your area.</p>
                  <Button variant="warning" onClick={() => loadDashboardData(location.latitude, location.longitude)}>
                    Refresh
                  </Button>
                </div>
              ) : (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Customer</th>
                      <th>Waste Type</th>
                      <th>Quantity</th>
                      <th>Address</th>
                      <th>Distance</th>
                      <th>Created</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pendingRequests.map((request) => (
                      <tr key={request.id}>
                        <td>
                          <strong>{request.customer_first_name} {request.customer_last_name}</strong>
                          <br />
                          <small className="text-muted">{request.customer_phone}</small>
                        </td>
                        <td>
                          <span className="text-capitalize">{request.waste_type}</span>
                        </td>
                        <td>{request.quantity || 'N/A'}</td>
                        <td className="text-truncate" style={{ maxWidth: '200px' }}>
                          {request.pickup_address}
                        </td>
                        <td>
                          {request.distance ? (
                            <Badge bg="info">{formatDistance(request.distance)}</Badge>
                          ) : (
                            <span className="text-muted">N/A</span>
                          )}
                        </td>
                        <td>
                          {new Date(request.created_at).toLocaleDateString()}
                        </td>
                        <td>
                          <Button 
                            size="sm" 
                            variant="success"
                            onClick={() => handleRequestStatusUpdate(request.id, 'assigned')}
                          >
                            Accept
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* My Assigned Requests */}
      <Row>
        <Col>
          <Card className="shadow-sm">
            <Card.Header className="bg-info text-white">
              <h5 className="mb-0">
                <i className="fas fa-tasks me-2"></i>
                My Assigned Requests
              </h5>
            </Card.Header>
            <Card.Body>
              {wasteRequests.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-inbox fa-3x text-muted mb-3"></i>
                  <p className="text-muted">No assigned requests. Accept some pending requests!</p>
                </div>
              ) : (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Customer</th>
                      <th>Waste Type</th>
                      <th>Quantity</th>
                      <th>Address</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {wasteRequests.map((request) => (
                      <tr key={request.id}>
                        <td>
                          <strong>{request.customer_first_name} {request.customer_last_name}</strong>
                          <br />
                          <small className="text-muted">{request.customer_phone}</small>
                        </td>
                        <td>
                          <span className="text-capitalize">{request.waste_type}</span>
                        </td>
                        <td>{request.quantity || 'N/A'}</td>
                        <td className="text-truncate" style={{ maxWidth: '200px' }}>
                          {request.pickup_address}
                        </td>
                        <td>
                          <Badge bg={getStatusBadgeVariant(request.status)}>
                            {request.status.replace('_', ' ').toUpperCase()}
                          </Badge>
                        </td>
                        <td>
                          {request.status === 'assigned' && (
                            <Button 
                              size="sm" 
                              variant="primary"
                              onClick={() => handleRequestStatusUpdate(request.id, 'in_progress')}
                            >
                              Start Collection
                            </Button>
                          )}
                          {request.status === 'in_progress' && (
                            <Button 
                              size="sm" 
                              variant="success"
                              onClick={() => handleRequestStatusUpdate(request.id, 'completed')}
                            >
                              Mark Complete
                            </Button>
                          )}
                          {request.status === 'completed' && (
                            <Badge bg="success">Completed</Badge>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </>
  );

  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '60vh' }}>
        <div className="text-center">
          <Spinner animation="border" variant="primary" className="mb-3" />
          <p>Loading dashboard...</p>
        </div>
      </Container>
    );
  }

  if (!user) {
    return (
      <Container className="py-5">
        <Alert variant="warning">
          Please log in to access the dashboard.
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {user.role === 'customer' ? customerDashboard() : collectorDashboard()}
      
      {/* Location Modal */}
      <Modal show={showLocationModal} onHide={() => setShowLocationModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Set Your Location</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>To find nearby waste collectors, we need your location. You can either:</p>
          <ul>
            <li>Allow location access in your browser</li>
            <li>Or enter your address manually below</li>
          </ul>
          
          <Form.Group className="mb-3">
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowLocationModal(false)}>
            Skip
          </Button>
          <Button variant="primary" onClick={handleManualLocation}>
            Set Location
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

const mapStateToProps = (state) => ({
  auth: state.auth
});

export default connect(mapStateToProps)(Dashboard);
